javaposse.jobdsl.plugin.ExecuteDslScript.scriptText:
def singleP = "${SINGLE_USE}" as Integer
                def jobCount = "${JOBCOUNT}" as Integer
                def agentLabel = "${AGENT_LABEL}" as String
                def everyMin = "${EVERY_XMIN}" as Integer

                Random random = new Random()

                (1..jobCount).each { c ->
                def jobName = "test-job-${c}"

                def j = job(jobName) {
                if (agentLabel) {
                label(agentLabel)
                }

                triggers {
                cron("*/${EVERY_XMIN} * * * *")
                }

                wrappers {
                if (Math.abs(random.nextInt() % 100) + 1 <= singleP) {
                configure { node ->
                node / 'buildWrappers' / 'org.jenkinsci.plugins.mesos.MesosSingleUseSlave'()
                }
                }
                }

                steps {
                shell("echo 'hello, world'; sleep 30")
                }
                }

                queue(j)
                }
